import React from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

const ButtonStyle = styled.div`

margin-top:2rem;
.button{
    font-size:2.2rem;
    background-color: ${(props) => 
        props.outline ? 'transparent' : 'var(--white)'
    };
    padding: 0.7em 2em;
    border-radius: 8px;
    display: inline-block;
    border:2px solid var(--gray-1);
    color:${(props) => 
        props.outline ? 'var(--gray-1)' : 'var(--black)'
    };
}
@media only screen and (max-width:760px){
    .button{
        font-size:1.8rem;
    }
}

`

export default function Button({btnLink = "test",btnText = "text",outline = false}) {
    return (
        <ButtonStyle  className="buttonWrapper" outline={outline}>
            <Link className="button" to={btnLink}>{btnText}</Link>
        </ButtonStyle>
    )
}
