import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import {MdMenu,MdClose,MdHome} from 'react-icons/md';
import { useState } from 'react';




const NavBarStyles = styled.div`

    position: fixed;
    z-index:100;
    top:0;
    left:0;

    width:100%;
    padding: 1rem 0;
    //background-color: rgba(37,37,37,0.92);
    background-color: rgba(30,30,30,0.94);

    .webName{
        float:left;
        padding:1rem;

        p{
            font-size: 20px;
        }
    }

    ul{
        max-width: 1200px;
        margin:0 auto;
        width: 90%;
        text-align: right;

        li {
            display: inline-block;

            border-radius: 8px;
            transition: .3s ease background-color;
            &:hover{
                background-color: var(--deep-dark);

            }

        }
        a {
            display: inline-block;
            font-family: -apple-system, sans-serif;
            padding: 1rem 2rem;
            font-size:2rem;
            color:var(--gray-1);
            outline: none;
        }
        .active{
            color:var(--white);
            text-align: left;
        }
    }
    .mobile-navbar-icon{
        position: absolute;
        right:1em;
        top:1em;
        width:2rem;
        cursor: pointer;
        display: none;
        outline: none;
        *{
            pointer-events: none;
        }



    }
    .closeNavBarIcon{
        display: none;
    }
    @media only screen and (max-width:760px){
        padding: 0;
        .mobile-navbar-icon{
            display: block;
        }
        .hide-item{
           transform:translateY(calc(-100% - var(--top)));
        }

    .webName{
       display: none;

    }
        .navBar{
            --top:1rem;
            transition: none.3s ease transform;
            //background-color: var(--deep-dark);
            background-color: rgba(30,30,30,0.94);
            padding: 2rem;
            width:90%;
            max-width: 300px;
            border-radius: 12px;
            position: absolute;
            right:1rem;
            top:var(--top);

            .closeNavBarIcon{
                display: block;
                width: 3rem;
                margin: 0 0 0 auto;
                cursor: pointer;
                *{
                    pointer-events: none;
                }
            }

            li{
                display: block;
                margin-bottom:1rem;
            }
        }


    }

`;

export default function NavBar() {
    const[showNavBar,setShowNavBar]= useState(false);
    return (
        <NavBarStyles>
       <div className="mobile-navbar-icon"   
       onClick={() => setShowNavBar(!showNavBar)} 
                role="button" 
                onKeyDown={()=> setShowNavBar(!showNavBar)} 
                tabIndex={0}>
       <MdMenu/>
       </div>

            <ul className={!showNavBar ? 'navBar hide-item':'navBar'}>

                <div className="webName" >
                    <p>Caven Lim</p>
                </div>

                <div className="closeNavBarIcon" 
                onClick={() => setShowNavBar(!showNavBar)} 
                role="button" 
                onKeyDown={()=> setShowNavBar(!showNavBar)} 
                tabIndex={0}>
                    <MdClose />
                </div>
                <li>


                    <NavLink to="/" exact  
                    onClick={() => setShowNavBar(!showNavBar)} 
                role="button" 
                onKeyDown={()=> setShowNavBar(!showNavBar)} 
                tabIndex={0}>Home</NavLink>
                </li>
                <li>
                    <NavLink to="/about"  
                    onClick={() => setShowNavBar(!showNavBar)} 
                role="button" 
                onKeyDown={()=> setShowNavBar(!showNavBar)} 
                tabIndex={0}>About</NavLink>
                </li>
                <li>
                    <NavLink to="/projects"  
                    onClick={() => setShowNavBar(!showNavBar)} 
                role="button" 
                onKeyDown={()=> setShowNavBar(!showNavBar)} 
                tabIndex={0}>Projects</NavLink>
                </li>

            </ul>
        </NavBarStyles>
    )
}
