import React from 'react'
import styled from 'styled-components'
import TaskCow2 from '../assets/Images/TaskCow2.png';
import Button from './Button';
import { Marginer } from './marginer';
import {GrAppleAppStore} from 'react-icons/gr';
import { NavLink } from 'react-router-dom';

import {FaArrowRight} from 'react-icons/fa';

const TaskCowBannerContainer = styled.div`

    width: 100%;
    height:390px;
    display: flex;
    background-image: linear-gradient(to bottom right, red, yellow);
    align-items: center;
    justify-content: center;
    position: relative;

.custom-shape-divider-top-1631952028 {
position: absolute;
top: 0;
left: 0;
width: 100%;
overflow: hidden;
line-height: 0;
}

.custom-shape-divider-top-1631952028 svg {
position: relative;
display: block;
width: calc(132% + 1.3px);
height: 84px;
transform: rotateY(180deg);
}

.custom-shape-divider-top-1631952028 .shape-fill {
fill: var(--deep-dark);
}

`

const ContentContainer = styled.div`

    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    gap:5rem;

    @media only screen and (max-width:760px){
        display: flex;
        flex-direction: column;
        gap:0rem;

    }

`;
const SloganContainer = styled.div`
    display: flex;
    flex-direction:column;

`;

const Slogan = styled.h2`
    margin: 0 ;

    font-size:20px;
    color:  var(--white);
    font-weight: 450;

    @media only screen and (max-width:760px){
        font-size:14px;

    }



`


const SloganTitle = styled.h2`
    margin: 0 ;
    margin-bottom:2rem;
    font-size:30px;
    color:  var(--white);
    font-weight: 700;




`

const ButtonsDivStyle = styled.div`
    display: flex;
    gap: 1rem;


`



const BtnGo = styled.button`

    background-color: transparent;
    border-radius:4rem;
    border: none; 
    color: white;
    width:3rem ;
    height:3rem ;
    font-size: 0.5rem; 
    cursor: pointer;




    path{
        color:var(--white);




    }


    @media only screen and (max-width:760px){
        align-self: center;
       justify-self: center;


    }




`

const ImageApp = styled.div`

    width: 20em;
    height: 19em;

    img{
        width: 100%;
        height: 100%;
    }

    @media only screen and (max-width:760px){


        float:top;
        margin-top: 5rem;
        width: 5em;
        height: 4.5em;


    }

`

// for new windows open url
function onClick(urlName){
    window.open(
        urlName,
        '_blank' //open in a new window.
      );

}
// for non new windows opener
function onClick2(urlName){
    window.location.href = urlName

}

export default function TaskCowBanner() {
    return (
        <TaskCowBannerContainer>
            <div class="custom-shape-divider-top-1631952028">
    <svg data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none">
        <path d="M1200 0L0 0 892.25 114.72 1200 0z" class="shape-fill"></path>
    </svg>
</div>
            <ContentContainer>
                <SloganContainer>
                   <Marginer direction="vertical" margin="1em"/>
                    <SloganContainer>
                        <SloganTitle>TaskCow</SloganTitle>
                        <Slogan>
                            Let your productivity go up with TaskCow
                        </Slogan>
                        <Slogan>
                            and your Home Screen
                        </Slogan>
                        <Slogan>
                            as you get reminded of tasks you set!
                        </Slogan>
                    </SloganContainer>
                    <Marginer direction="vertical" margin={20}/>
                    <ButtonsDivStyle className="Buttons">
                    <BtnGo id="bt" onClick={() => onClick('https://apps.apple.com/sg/app/taskcow/id1565968348')} outline={false}><GrAppleAppStore/></BtnGo>
                    <BtnGo id="bt2" onClick={() => onClick2('/#/taskcow')}outline={false}><FaArrowRight/></BtnGo>
                    </ButtonsDivStyle>

                </SloganContainer>
                <ImageApp>
                    <img src={TaskCow2} alt="" />
                </ImageApp>
            </ContentContainer>

        </TaskCowBannerContainer>
    )
}
