import React from 'react'
import styled from 'styled-components'
import doggo from '../assets/Images/doggo.png';
import Button from './Button';
import { Marginer } from './marginer';
import {GrAppleAppStore} from 'react-icons/gr';
import { NavLink } from 'react-router-dom';

import {FaArrowRight} from 'react-icons/fa';

const TaskCowBannerContainer = styled.div`

    width: 100%;
    height:390px;
    display: flex;
    background-image: linear-gradient(to bottom right, #e75480, pink);
    align-items: center;
    justify-content: center;

`

const ContentContainer = styled.div`

    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    gap:5rem;

    @media only screen and (max-width:760px){
        display: flex;
        flex-direction: column;
        gap:0rem;

    }

`;
const SloganContainer = styled.div`
    display: flex;
    flex-direction:column;

`;

const Curve = styled.div`
    position: absolute;
    height: 225px;
    width: 100%;
    bottom: 0;

    .Curve::before{
        content: '';
        display: block;
        position: absolute;
        border-radius: 100% 50%;
        width: 55%;
        height: 100%;
        background-color:#202731;
        transform: translate(85%,60%);
    }

    .Curve::after{
        content: '';
        display: block;
        position: absolute;
        border-radius: 100% 50%;
        width: 55%;
        height: 100%;
        background-color:#3c31dd;
        transform: translate(-4%,40%);
        z-index:1;
    }



`

const Slogan = styled.h2`
    margin: 0 ;

    font-size:20px;
    color:  var(--white);
    font-weight: 450;

    @media only screen and (max-width:760px){
        font-size:14px;

    }



`


const SloganTitle = styled.h2`
    margin: 0 ;
    margin-bottom:2rem;
    font-size:30px;
    color:  var(--white);
    font-weight: 700;




`

const ButtonsDivStyle = styled.div`
    display: flex;
    gap: 1rem;


`



const BtnGo = styled.button`

    background-color: transparent;
    border-radius:4rem;
    border: none; 
    color: white;
    width:3rem ;
    height:3rem ;
    font-size: 0.5rem; 
    cursor: pointer;




    path{
        color:var(--white);




    }


    @media only screen and (max-width:760px){
        align-self: center;
       justify-self: center;


    }




`

const ImageApp = styled.div`

    width: 20em;
    height: 19em;

    img{
        width: 100%;
        height: 100%;
    }

    @media only screen and (max-width:760px){


        float:top;
        margin-top: 5rem;
        width: 5em;
        height: 4.5em;


    }

`

// for new windows open url
function onClick(urlName){
    window.open(
        urlName,
        '_blank' //open in a new window.
      );

}
// for non new windows opener
function onClick2(urlName){
    window.location.href = urlName

}

export default function DogCommBanner() {
    return (

        <TaskCowBannerContainer>
            <Curve/>
            <ContentContainer>
                <SloganContainer>
                   <Marginer direction="vertical" margin="1em"/>
                    <SloganContainer>
                        <SloganTitle>DogComm</SloganTitle>
                        <Slogan>
                            Gather all the dog lovers/owners
                        </Slogan>
                        <Slogan>
                            to celebrate our doggos
                        </Slogan>
                        <Slogan>
                            as while as gather a community!
                        </Slogan>
                    </SloganContainer>
                    <Marginer direction="vertical" margin={20}/>
                    <ButtonsDivStyle className="Buttons">
                    <BtnGo id="bt" onClick={() => onClick('https://testflight.apple.com/join/op0mYbS9')} outline={false}><GrAppleAppStore/></BtnGo>
                    <BtnGo id="bt2" onClick={() => onClick2('/#/dogcomm')}outline={false}><FaArrowRight/></BtnGo>
                    </ButtonsDivStyle>

                </SloganContainer>
                <ImageApp>
                    <img src={doggo} alt="" />
                </ImageApp>
            </ContentContainer>
        </TaskCowBannerContainer>

    )
}
